action_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilelist_1': 'action_mobilelist_1',
        'mobilelistitem_2': 'action_mobilelistitem_2',
        'mobilelistitembutton_3': 'action_mobilelistitembutton_3',
        'mobilelistitem_4': 'action_mobilelistitem_4',
        'mobilelistitembutton_5': 'action_mobilelistitembutton_5',
        'mobilelistitem_6': 'action_mobilelistitem_6',
        'mobilelistitembutton_7': 'action_mobilelistitembutton_7',
        'mobilelistitem_8': 'action_mobilelistitem_8',
        'mobilelistitembutton_9': 'action_mobilelistitembutton_9'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fixing issue https://github.com/jquery/jquery-mobile/issues/5424 on Windows Phone
        $("div[data-role=page] div[data-role=footer]").css("bottom", "-36px");
    }

    if (Appery.getTargetPlatform == "I") {
        $.each(window.n2id, function(name, id) {
            var elApperyRole = $("#" + id).attr("data-role");
            if (elApperyRole != "appery_label" && elApperyRole != "appery_link") {
                var appleDataSelector = "#" + id + "[x-apple-data-detectors], #" + id + " [x-apple-data-detectors]";
                $(appleDataSelector).attr("x-apple-data-detectors", "false");
            }
        });
    }

    Appery.CurrentScreen = 'action';

    /*
     * Nonvisual components
     */
    var datasources = [];

    /*
     * Events and handlers
     */

    // Before Show
    action_beforeshow = function() {
        Appery.CurrentScreen = "action";
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }

    // On Load
    screen_F6D7_onLoad = action_onLoad = function() {
        screen_F6D7_elementsExtraJS();

        // TODO fire device events only if necessary (with JS logic)
        action_deviceEvents();
        action_windowEvents();
        screen_F6D7_elementsEvents();
    }

    // screen window events
    screen_F6D7_windowEvents = action_windowEvents = function() {

        $('#action').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    }

    // device events
    action_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

        });
    }

    // screen elements extra js
    screen_F6D7_elementsExtraJS = action_elementsExtraJS = function() {
        // screen (action) extra code

        /* mobilelist_1 */

        listView = $("#action_mobilelist_1");
        theme = listView.attr("data-theme");
        if (typeof theme !== 'undefined') {
            var themeClass = "ui-btn-up-" + theme;
            listItem = $("#action_mobilelist_1 .ui-li-static");
            $.each(listItem, function(index, value) {
                $(this).addClass(themeClass);
            });
        }

        /* mobilelistitem_2 */

        /* mobilelistitem_4 */

        /* mobilelistitem_6 */

        /* mobilelistitem_8 */

    }

    // screen elements handler
    screen_F6D7_elementsEvents = action_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

        $('#action_mobilecontainer [name="mobilelistitem_2"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    window.top.location = 'https://www.itriagehealth.com/search';

                }
            },
        });

        $('#action_mobilecontainer [name="mobilelistitem_4"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    window.top.location = 'https://www.healthwise.net/aetna/find/search.aspx?searchTerm=imaging&topicSearch=Search';

                }
            },
        });

        $('#action_mobilecontainer [name="mobilelistitem_6"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    window.top.location = 'http://www.choosingwisely.org/doctor-patient-lists/american-college-of-physicians/';

                }
            },
        });

        $('#action_mobilecontainer [name="mobilelistitem_8"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    Appery.navigateTo('map2', {
                        reverse: false
                    });

                }
            },
        });

    }

    $("#action").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        action_beforeshow();
    });

    if (runBeforeShow) {
        action_beforeshow();
    } else {
        action_onLoad();
    }

}

